import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  selectNetworkStatus,
  selectIsCapturing,
  selectFilteredPackets,
  selectStats,
  selectBandwidth,
  selectFilters,
  selectSelectedPacket,
  connectToCapture,
  startCapture,
  stopCapture,
  setFilter,
  selectPacket,
  clearPackets,
} from '../features/network/networkSlice';

const NetworkMonitor = () => {
  const dispatch = useDispatch();
  const status = useSelector(selectNetworkStatus);
  const isCapturing = useSelector(selectIsCapturing);
  const packets = useSelector(selectFilteredPackets);
  const stats = useSelector(selectStats);
  const bandwidth = useSelector(selectBandwidth);
  const filters = useSelector(selectFilters);
  const selectedPacket = useSelector(selectSelectedPacket);

  useEffect(() => {
    // Initialize WebSocket connection when component mounts
    dispatch(connectToCapture());

    // Clean up on unmount
    return () => {
      // Clean up WebSocket connection if needed
    };
  }, [dispatch]);

  const handleStartCapture = () => {
    dispatch(startCapture());
  };

  const handleStopCapture = () => {
    dispatch(stopCapture());
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    dispatch(setFilter({ [name]: value }));
  };

  const handleClearPackets = () => {
    dispatch(clearPackets());
  };

  const formatBytes = (bytes, decimals = 2) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const dm = decimals < 0 ? 0 : decimals;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
  };

  return (
    <div className="space-y-6">
      {/* Control Panel */}
      <div className="bg-white shadow rounded-lg p-4">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between space-y-4 sm:space-y-0">
          <div className="flex items-center space-x-4">
            <h2 className="text-lg font-medium">Network Monitor</h2>
            <div className="flex items-center">
              <div className={`w-3 h-3 rounded-full mr-2 ${isCapturing ? 'bg-green-500' : 'bg-red-500'}`}></div>
              <span className="text-sm text-gray-600">
                {isCapturing ? 'Capturing...' : 'Stopped'}
              </span>
            </div>
          </div>
          <div className="flex space-x-3">
            {!isCapturing ? (
              <button
                onClick={handleStartCapture}
                className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
                disabled={status !== 'connected'}
              >
                Start Capture
              </button>
            ) : (
              <button
                onClick={handleStopCapture}
                className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
              >
                Stop Capture
              </button>
            )}
            <button
              onClick={handleClearPackets}
              className="px-4 py-2 border border-gray-300 text-gray-700 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
            >
              Clear
            </button>
          </div>
        </div>

        {/* Stats */}
        <div className="mt-6 grid grid-cols-2 gap-4 sm:grid-cols-3 lg:grid-cols-6">
          <div className="bg-gray-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-gray-500">Total Packets</p>
            <p className="mt-1 text-2xl font-semibold">{stats.total}</p>
          </div>
          <div className="bg-gray-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-gray-500">TCP</p>
            <p className="mt-1 text-2xl font-semibold">{stats.tcp}</p>
          </div>
          <div className="bg-gray-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-gray-500">UDP</p>
            <p className="mt-1 text-2xl font-semibold">{stats.udp}</p>
          </div>
          <div className="bg-gray-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-gray-500">HTTP/HTTPS</p>
            <p className="mt-1 text-2xl font-semibold">{stats.http + stats.https}</p>
          </div>
          <div className="bg-gray-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-gray-500">DNS</p>
            <p className="mt-1 text-2xl font-semibold">{stats.dns}</p>
          </div>
          <div className="bg-gray-50 p-4 rounded-lg">
            <p className="text-sm font-medium text-gray-500">Bandwidth</p>
            <p className="mt-1 text-sm font-semibold">
              {formatBytes(bandwidth.in)}/s in â€¢ {formatBytes(bandwidth.out)}/s out
            </p>
          </div>
        </div>

        {/* Filters */}
        <div className="mt-6 grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
          <div>
            <label htmlFor="protocol" className="block text-sm font-medium text-gray-700">Protocol</label>
            <select
              id="protocol"
              name="protocol"
              value={filters.protocol}
              onChange={handleFilterChange}
              className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
            >
              <option value="all">All Protocols</option>
              <option value="tcp">TCP</option>
              <option value="udp">UDP</option>
              <option value="http">HTTP</option>
              <option value="https">HTTPS</option>
              <option value="dns">DNS</option>
            </select>
          </div>
          <div>
            <label htmlFor="sourceIp" className="block text-sm font-medium text-gray-700">Source IP</label>
            <input
              type="text"
              id="sourceIp"
              name="sourceIp"
              value={filters.sourceIp}
              onChange={handleFilterChange}
              placeholder="192.168.1.1"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
          <div>
            <label htmlFor="destinationIp" className="block text-sm font-medium text-gray-700">Destination IP</label>
            <input
              type="text"
              id="destinationIp"
              name="destinationIp"
              value={filters.destinationIp}
              onChange={handleFilterChange}
              placeholder="192.168.1.100"
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
          <div>
            <label htmlFor="port" className="block text-sm font-medium text-gray-700">Port</label>
            <input
              type="text"
              id="port"
              name="port"
              value={filters.port}
              onChange={handleFilterChange}
              placeholder="80, 443, etc."
              className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </div>
        </div>
      </div>

      {/* Packets Table */}
      <div className="bg-white shadow rounded-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Time
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Source
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Destination
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Protocol
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Length
                </th>
                <th scope="col" className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Info
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {packets.length > 0 ? (
                packets.map((packet, index) => (
                  <tr 
                    key={index} 
                    className={`hover:bg-gray-50 cursor-pointer ${selectedPacket === packet ? 'bg-blue-50' : ''}`}
                    onClick={() => dispatch(selectPacket(packet))}
                  >
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {new Date(packet.timestamp).toLocaleTimeString()}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {packet.source}
                      {packet.sourcePort && `:${packet.sourcePort}`}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {packet.destination}
                      {packet.destinationPort && `:${packet.destinationPort}`}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                        ${packet.protocol?.toLowerCase() === 'tcp' ? 'bg-blue-100 text-blue-800' : 
                          packet.protocol?.toLowerCase() === 'udp' ? 'bg-green-100 text-green-800' :
                          packet.protocol?.toLowerCase() === 'http' ? 'bg-yellow-100 text-yellow-800' :
                          packet.protocol?.toLowerCase() === 'https' ? 'bg-purple-100 text-purple-800' :
                          packet.protocol?.toLowerCase() === 'dns' ? 'bg-pink-100 text-pink-800' :
                          'bg-gray-100 text-gray-800'}`}>
                        {packet.protocol || 'OTHER'}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                      {packet.length ? formatBytes(packet.length) : 'N/A'}
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-500 truncate max-w-xs">
                      {packet.info || 'No additional info'}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="px-6 py-4 text-center text-sm text-gray-500">
                    {isCapturing ? 'No packets captured yet. Waiting for network traffic...' : 'Capture is not running. Click "Start Capture" to begin.'}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Packet Details Panel */}
      {selectedPacket && (
        <div className="bg-white shadow rounded-lg overflow-hidden">
          <div className="px-4 py-5 border-b border-gray-200 sm:px-6">
            <h3 className="text-lg leading-6 font-medium text-gray-900">Packet Details</h3>
          </div>
          <div className="px-4 py-5 sm:p-6">
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-2">
              <div>
                <h4 className="text-sm font-medium text-gray-500">Source</h4>
                <p className="mt-1 text-sm text-gray-900">
                  {selectedPacket.source}
                  {selectedPacket.sourcePort && `:${selectedPacket.sourcePort}`}
                </p>
              </div>
              <div>
                <h4 className="text-sm font-medium text-gray-500">Destination</h4>
                <p className="mt-1 text-sm text-gray-900">
                  {selectedPacket.destination}
                  {selectedPacket.destinationPort && `:${selectedPacket.destinationPort}`}
                </p>
              </div>
              <div>
                <h4 className="text-sm font-medium text-gray-500">Protocol</h4>
                <p className="mt-1 text-sm text-gray-900">{selectedPacket.protocol || 'N/A'}</p>
              </div>
              <div>
                <h4 className="text-sm font-medium text-gray-500">Length</h4>
                <p className="mt-1 text-sm text-gray-900">
                  {selectedPacket.length ? formatBytes(selectedPacket.length) : 'N/A'}
                </p>
              </div>
              <div className="sm:col-span-2">
                <h4 className="text-sm font-medium text-gray-500">Timestamp</h4>
                <p className="mt-1 text-sm text-gray-900">
                  {new Date(selectedPacket.timestamp).toLocaleString()}
                </p>
              </div>
              {selectedPacket.info && (
                <div className="sm:col-span-2">
                  <h4 className="text-sm font-medium text-gray-500">Additional Info</h4>
                  <p className="mt-1 text-sm text-gray-900 break-all">{selectedPacket.info}</p>
                </div>
              )}
            </div>

            {selectedPacket.rawData && (
              <div className="mt-6">
                <h4 className="text-sm font-medium text-gray-500 mb-2">Raw Data</h4>
                <div className="bg-gray-50 p-4 rounded-md overflow-x-auto">
                  <pre className="text-xs text-gray-800 font-mono">
                    {JSON.stringify(selectedPacket.rawData, null, 2)}
                  </pre>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default NetworkMonitor;
