import { configureStore } from '@reduxjs/toolkit';
import networkReducer, { connectToBackend } from '../services/networkService';
import { webSocketService } from '../services/websocketService';

export const store = configureStore({
  reducer: {
    network: networkReducer,
  },
  middleware: (getDefaultMiddleware) =>
    getDefaultMiddleware({
      thunk: {
        extraArgument: { webSocketService },
      },
      serializableCheck: {
        // Ignore these action types
        ignoredActions: ['network/connect/fulfilled'],
        // Ignore these field paths in all actions
        ignoredActionPaths: ['meta.arg', 'payload.timestamp'],
        // Ignore these paths in the state
        ignoredPaths: ['network.packets'],
      },
    }),
});

// Initialize WebSocket connection when the store is created
store.dispatch(connectToBackend());
