import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { 
  selectNetworkStatus, 
  selectIsCapturing, 
  selectInterfaces, 
  selectActiveInterface,
  setActiveInterface,
  connectToCapture,
  startCapture,
  stopCapture,
  clearPackets
} from '../features/network/networkSlice';
import NetworkMonitor from '../components/NetworkMonitor';

const NetworkMonitorPage = () => {
  const dispatch = useDispatch();
  const status = useSelector(selectNetworkStatus);
  const isCapturing = useSelector(selectIsCapturing);
  const interfaces = useSelector(selectInterfaces);
  const activeInterface = useSelector(selectActiveInterface);

  // Initialize WebSocket connection when component mounts
  useEffect(() => {
    dispatch(connectToCapture());

    // Cleanup on unmount
    return () => {
      if (isCapturing) {
        dispatch(stopCapture());
      }
    };
  }, [dispatch, isCapturing]);

  const handleInterfaceChange = (e) => {
    const selectedInterface = e.target.value;
    dispatch(setActiveInterface(selectedInterface));

    // If we were capturing, restart capture on the new interface
    if (isCapturing) {
      dispatch(stopCapture()).then(() => {
        dispatch(startCapture());
      });
    }
  };

  const handleStartCapture = () => {
    if (!activeInterface) {
      alert('Please select a network interface first');
      return;
    }
    dispatch(startCapture());
  };

  const handleStopCapture = () => {
    dispatch(stopCapture());
  };

  const handleClearPackets = () => {
    dispatch(clearPackets());
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="bg-white shadow rounded-lg p-6">
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center mb-6">
              <h1 className="text-2xl font-semibold text-gray-900">Network Traffic Monitor</h1>

              <div className="mt-4 sm:mt-0 flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-2">
                <div className="relative">
                  <select
                    id="interface"
                    value={activeInterface || ''}
                    onChange={handleInterfaceChange}
                    className="block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                    disabled={isCapturing}
                  >
                    <option value="" disabled>Select Interface</option>
                    {interfaces.map((iface) => (
                      <option key={iface.name} value={iface.name}>
                        {iface.name} {iface.description ? `(${iface.description})` : ''}
                      </option>
                    ))}
                  </select>
                </div>

                {!isCapturing ? (
                  <button
                    onClick={handleStartCapture}
                    disabled={!activeInterface || status === 'connecting'}
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Start Capture
                  </button>
                ) : (
                  <button
                    onClick={handleStopCapture}
                    className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                  >
                    Stop Capture
                  </button>
                )}

                <button
                  onClick={handleClearPackets}
                  disabled={isCapturing}
                  className="inline-flex items-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Clear
                </button>
              </div>
            </div>

            <NetworkMonitor />
          </div>
        </div>
      </div>
    </div>
  );
};

export default NetworkMonitorPage;
