import React, { useEffect, useState } from 'react';

class WebSocketService {
  constructor() {
    this.socket = null;
    this.messageHandlers = new Set();
    this.errorHandlers = new Set();
    this.reconnectAttempts = 0;
    this.maxReconnectAttempts = 5;
    this.reconnectDelay = 1000; // Start with 1 second
    this.maxReconnectDelay = 10000; // Max 10 seconds
    this.connectionStatus = 'disconnected';
    this.connectionStatusListeners = new Set();
    this.reconnectTimeout = null;
    this.url = null;
    this.dispatch = null;
  }

  // Set the Redux dispatch function
  setDispatch(dispatch) {
    this.dispatch = dispatch;
  }

  connect(url) {
    this.url = url;
    console.log(`[WebSocket] Initializing connection to ${url}`);

    return new Promise((resolve, reject) => {
      try {
        // Close existing connection if any
        if (this.socket) {
          console.log('[WebSocket] Closing existing connection...');
          this.disconnect();
        }

        console.log(`[WebSocket] Creating new WebSocket instance to ${url}`);
        this.socket = new WebSocket(url);
        this.updateConnectionStatus('connecting');

        // Notify Redux store about connection attempt
        if (this.dispatch) {
          this.dispatch({ type: 'network/updateCaptureStatus', payload: { status: 'connecting' } });
        }

        // Bind event handlers
        this.socket.onopen = (event) => {
          console.log('[WebSocket] Connection established', {
            url: this.socket.url,
            readyState: this.socket.readyState,
            extensions: this.socket.extensions,
            protocol: this.socket.protocol
          });
          this.reconnectAttempts = 0;
          this.reconnectDelay = 1000;
          this.updateConnectionStatus('connected');

          // Notify Redux store about successful connection
          if (this.dispatch) {
            this.dispatch({ type: 'network/updateCaptureStatus', payload: { status: 'connected' } });
          }

          resolve();
        };

        this.socket.onerror = (error) => {
          const errorMessage = `[WebSocket] Connection error: ${error.message || 'Unknown error'}`;
          console.error(errorMessage, {
            readyState: this.socket?.readyState,
            url: this.socket?.url,
            error
          });
          this.notifyErrorHandlers(error);
          this.updateConnectionStatus('error');

          // Only reject if this is the initial connection attempt
          if (this.reconnectAttempts === 0) {
            const wsError = new Error(errorMessage);
            console.error('[WebSocket] Rejecting connection promise', wsError);
            reject(wsError);
          }
        };

        this.socket.onmessage = (event) => {
          console.debug('[WebSocket] Raw message received:', event.data);
          try {
            if (!event.data) {
              console.debug('[WebSocket] Received empty message, ignoring');
              return;
            }
            this.processMessage(event.data);
          } catch (error) {
            console.error('[WebSocket] Error processing message:', {
              error: error.message,
              stack: error.stack,
              data: event.data
            });
            this.notifyErrorHandlers(error);
          }
        };

        this.socket.onclose = (event) => {
          console.log('[WebSocket] Connection closed', {
            code: event.code,
            reason: event.reason,
            wasClean: event.wasClean
          });

          const newStatus = 'disconnected';
          this.updateConnectionStatus(newStatus);

          // Notify Redux store about disconnection
          if (this.dispatch) {
            this.dispatch({ type: 'network/updateCaptureStatus', payload: { status: newStatus } });
          }

          this.attemptReconnect();
        };
      } catch (error) {
        console.error('WebSocket Connection Error:', error);
        reject(error);
      }
    });
  }

  updateConnectionStatus(status) {
    this.connectionStatus = status;
    this.notifyStatusChange(status);
  }

  onStatusChange(callback) {
    this.connectionStatusListeners.add(callback);
    return () => this.connectionStatusListeners.delete(callback);
  }

  notifyStatusChange(status) {
    this.connectionStatusListeners.forEach(callback => {
      try {
        callback(status);
      } catch (error) {
        console.error('Error in status change handler:', error);
      }
    });
  }

  getStatus() {
    return this.connectionStatus;
  }

  disconnect() {
    if (this.socket) {
      this.socket.close();
      this.socket = null;
    }
    this.messageHandlers.clear();
  }

  send(message) {
    if (this.socket && this.socket.readyState === WebSocket.OPEN) {
      try {
        const messageString = typeof message === 'string' ? message : JSON.stringify(message);
        this.socket.send(messageString);
        return true;
      } catch (error) {
        console.error('Error sending WebSocket message:', error);
        return false;
      }
    }
    console.warn('WebSocket is not connected');
    return false;
  }

  addMessageHandler(handler) {
    this.messageHandlers.add(handler);

    // Return cleanup function
    return () => this.messageHandlers.delete(handler);
  }

  removeMessageHandler(handler) {
    this.messageHandlers.delete(handler);
  }

  processMessage(message) {
    // Skip empty messages
    if (!message || typeof message !== 'string' || message.trim() === '') {
      console.warn('Received empty WebSocket message');
      return;
    }

    let data;
    try {
      data = JSON.parse(message);
      console.debug('Received WebSocket message:', data);
    } catch (error) {
      console.error('Failed to parse WebSocket message:', {
        error: error.message,
        message: message
      });
      return;
    }

    // Handle different message formats
    try {
      // Notify all message handlers
      this.messageHandlers.forEach(handler => {
        try {
          handler(data);
        } catch (error) {
          console.error('Error in message handler:', error);
        }
      });

      // Dispatch to Redux if it's a packet or status update
      if (this.dispatch) {
        // Handle different message formats
        if (data.type) {
          // Format: { type: 'packet', data: {...} }
          if (data.type === 'packet' && data.data) {
            this.dispatch({ type: 'network/addPacket', payload: data.data });
          } else if (data.type === 'status') {
            this.dispatch({ type: 'network/updateCaptureStatus', payload: data.data || data });
          } else if (data.type === 'interfaces') {
            this.dispatch({ type: 'network/updateInterfaces', payload: data.data || data });
          } else if (data.type === 'stats') {
            this.dispatch({ type: 'network/updateStats', payload: data.data || data });
          } else if (data.type === 'bandwidth') {
            this.dispatch({ type: 'network/updateBandwidth', payload: data.data || data });
          }
        } else if (data.protocol) {
          // Format: Direct packet object
          this.dispatch({ type: 'network/addPacket', payload: data });
        } else if (data.isCapturing !== undefined) {
          // Format: Direct status object
          this.dispatch({ type: 'network/updateCaptureStatus', payload: data });
        }
      }
    } catch (error) {
      console.error('Error processing WebSocket message:', {
        error: error.message,
        stack: error.stack,
        message: message,
        parsedData: data
      });
    }
  }

  onError(handler) {
    if (typeof handler === 'function') {
      this.errorHandlers.add(handler);
      return () => this.errorHandlers.delete(handler);
    }
    return () => { };
  }

  removeErrorHandler(handler) {
    this.errorHandlers.delete(handler);
  }

  notifyErrorHandlers(error) {
    this.errorHandlers.forEach(handler => {
      try {
        handler(error);
      } catch (err) {
        console.error('Error in error handler:', err);
      }
    });
  }

  attemptReconnect() {
    this.reconnectAttempts++;
    this.reconnectDelay = Math.min(this.reconnectDelay * 2, this.maxReconnectDelay);
    this.reconnectTimeout = setTimeout(() => {
      this.connect(this.url);
    }, this.reconnectDelay);
  }
}

// Export a singleton instance
export const webSocketService = new WebSocketService();

// Helper hook for React components
export const useWebSocket = (onMessage, dependencies = []) => {
  const [isConnected, setIsConnected] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    const handleMessage = (message) => {
      if (message.type === 'connection/established') {
        setIsConnected(true);
        setError(null);
      } else if (message.type === 'connection/error') {
        setError(message.payload);
      }

      if (onMessage) {
        onMessage(message);
      }
    };

    // Add the handler
    const removeHandler = webSocketService.addMessageHandler(handleMessage);

    // Clean up
    return () => {
      removeHandler();
    };
  }, [onMessage, ...dependencies]);
};
