import React, { useEffect, useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import ErrorBoundary from './ErrorBoundary';
import PermissionRequest from './PermissionRequest';
import { 
  connectToBackend, 
  startCapture, 
  stopCapture,
  applyMitigation,
  selectIsConnected,
  selectIsCapturing,
  selectInterfaces,
  selectCurrentInterface,
  selectPackets,
  selectStats,
  selectBandwidth,
  selectDetectedAttacks,
  selectActiveMitigations,
  selectError,
  selectFilteredPackets
} from '../services/networkService';

// Default stats object to prevent undefined access
const defaultStats = {
  packetCount: 0,
  protocols: {},
  sourceIps: {},
  destinationIps: {}
};

// Default bandwidth object
const defaultBandwidth = {
  current: { in: 0, out: 0 },
  total: { in: 0, out: 0 }
};

const NetworkMonitorContent = () => {
  const dispatch = useDispatch();
  const [selectedInterface, setSelectedInterface] = useState('');
  const [packetFilter, setPacketFilter] = useState('');
  const [activeTab, setActiveTab] = useState('packets');

  // Selectors with default values
  const isConnected = useSelector(selectIsConnected);
  const isCapturing = useSelector(selectIsCapturing);
  const interfaces = useSelector(selectInterfaces) || [];
  const currentInterface = useSelector(selectCurrentInterface);
  const packets = useSelector(selectFilteredPackets) || [];
  const stats = useSelector(selectStats) || defaultStats;
  const bandwidth = useSelector(selectBandwidth) || defaultBandwidth;
  const detectedAttacks = useSelector(selectDetectedAttacks) || [];
  const activeMitigations = useSelector(selectActiveMitigations) || [];
  const error = useSelector(selectError);

  // Connect to backend on component mount and handle reconnection
  useEffect(() => {
    console.log('NetworkMonitor mounted, isConnected:', isConnected);

    if (!isConnected) {
      console.log('Initiating WebSocket connection...');

      const connect = () => {
        console.log('Dispatching connectToBackend action...');
        dispatch(connectToBackend())
          .then(() => {
            console.log('WebSocket connection established successfully');
          })
          .catch(error => {
            console.error('Connection error:', error);
            // Auto-retry after 5 seconds
            console.log('Scheduling reconnection in 5 seconds...');
            const timeoutId = setTimeout(connect, 5000);
            return () => clearTimeout(timeoutId);
          });
      };

      connect();

      return () => {
        console.log('Cleaning up WebSocket connection...');
      };
    } else {
      console.log('WebSocket already connected');
    }
  }, [dispatch, isConnected]);

  // Auto-select first interface if none selected
  useEffect(() => {
    if (interfaces.length > 0 && !selectedInterface) {
      setSelectedInterface(interfaces[0].name);
    }
  }, [interfaces, selectedInterface]);

  const handleStartCapture = () => {
    if (selectedInterface) {
      dispatch(startCapture(selectedInterface));
    }
  };

  const handleStopCapture = () => {
    dispatch(stopCapture());
  };

  const handleMitigate = (target, action) => {
    dispatch(applyMitigation({ target, action }));
  };

  const formatBytes = (bytes, decimals = 2) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const dm = decimals < 0 ? 0 : decimals;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
  };

  const formatBps = (bps) => {
    if (bps < 1000) return `${bps.toFixed(2)} bps`;
    if (bps < 1000000) return `${(bps / 1000).toFixed(2)} Kbps`;
    return `${(bps / 1000000).toFixed(2)} Mbps`;
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Network Traffic Monitor</h1>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      {/* Controls */}
      <div className="bg-white rounded-lg shadow p-4 mb-6">
        <div className="flex flex-wrap gap-4 items-end">
          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Network Interface
            </label>
            <select
              className="w-full p-2 border rounded"
              value={selectedInterface}
              onChange={(e) => setSelectedInterface(e.target.value)}
              disabled={isCapturing}
            >
              {interfaces.map((iface) => (
                <option key={iface.name} value={iface.name}>
                  {iface.name} ({iface.ip})
                </option>
              ))}
            </select>
          </div>

          <div className="flex-1">
            <label className="block text-sm font-medium text-gray-700 mb-1">
              Filter (BPF format)
            </label>
            <input
              type="text"
              className="w-full p-2 border rounded"
              value={packetFilter}
              onChange={(e) => setPacketFilter(e.target.value)}
              placeholder="e.g., tcp port 80"
              disabled={isCapturing}
            />
          </div>

          <div className="flex gap-2">
            {!isCapturing ? (
              <button
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 disabled:bg-gray-400"
                onClick={handleStartCapture}
                disabled={!selectedInterface}
              >
                Start Capture
              </button>
            ) : (
              <button
                className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                onClick={handleStopCapture}
              >
                Stop Capture
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
        <div className="bg-white rounded-lg shadow p-4">
          <h3 className="text-lg font-semibold mb-2">Traffic Statistics</h3>
          <div className="space-y-1">
            <p>Total Packets: {(stats?.packetCount || 0).toLocaleString()}</p>
            <p>TCP Packets: {stats?.protocols?.tcp?.toLocaleString() || 0}</p>
            <p>UDP Packets: {stats?.protocols?.udp?.toLocaleString() || 0}</p>
            <p>ICMP Packets: {stats?.protocols?.icmp?.toLocaleString() || 0}</p>
            <p>Unique Sources: {Object.keys(stats?.sourceIps || {}).length}</p>
            <p>Unique Destinations: {Object.keys(stats?.destinationIps || {}).length}</p>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-4">
          <h3 className="text-lg font-semibold mb-2">Bandwidth</h3>
          <div className="space-y-1">
            <p>Incoming: {bandwidth ? formatBps((bandwidth.bpsReceived || 0) * 8) : '0 bps'}</p>
            <p>Outgoing: {bandwidth ? formatBps((bandwidth.bpsSent || 0) * 8) : '0 bps'}</p>
            <p>Total In: {bandwidth ? formatBytes(bandwidth.totalReceived || 0) : '0 B'}</p>
            <p>Total Out: {bandwidth ? formatBytes(bandwidth.totalSent || 0) : '0 B'}</p>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-4">
          <h3 className="text-lg font-semibold mb-2">Status</h3>
          <div className="space-y-1">
            <p>Connection: {isConnected ? 'Connected' : 'Disconnected'}</p>
            <p>Capture: {isCapturing ? 'Running' : 'Stopped'}</p>
            <p>Interface: {currentInterface || 'None'}</p>
            <p>Active Mitigations: {Object.keys(activeMitigations).length}</p>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="bg-white rounded-lg shadow overflow-hidden mb-6">
        <div className="flex border-b">
          <button
            className={`px-4 py-2 font-medium ${activeTab === 'packets' ? 'border-b-2 border-blue-500 text-blue-600' : 'text-gray-600'}`}
            onClick={() => setActiveTab('packets')}
          >
            Packets
          </button>
          <button
            className={`px-4 py-2 font-medium ${activeTab === 'attacks' ? 'border-b-2 border-red-500 text-red-600' : 'text-gray-600'}`}
            onClick={() => setActiveTab('attacks')}
          >
            Detected Attacks ({detectedAttacks.length})
          </button>
          <button
            className={`px-4 py-2 font-medium ${activeTab === 'mitigations' ? 'border-b-2 border-green-500 text-green-600' : 'text-gray-600'}`}
            onClick={() => setActiveTab('mitigations')}
          >
            Active Mitigations
          </button>
        </div>

        <div className="p-4">
          {activeTab === 'packets' && (
            <div className="overflow-auto max-h-96">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Time</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Source</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Destination</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Protocol</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Length</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Info</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {packets.map((pkt, i) => (
                    <tr key={i} className="hover:bg-gray-50">
                      <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-900">
                        {new Date(pkt.timestamp).toLocaleTimeString()}
                      </td>
                      <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-900">
                        {pkt.src}
                        {pkt.info?.sport ? `:${pkt.info.sport}` : ''}
                      </td>
                      <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-900">
                        {pkt.info?.dip || 'N/A'}
                        {pkt.info?.dport ? `:${pkt.info.dport}` : ''}
                      </td>
                      <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-900">
                        {(pkt.protocol || 'N/A').toUpperCase()}
                      </td>
                      <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-900">
                        {pkt.length}
                      </td>
                      <td className="px-6 py-2 whitespace-nowrap text-sm text-gray-900">
                        {pkt.info?.flags || pkt.info?.type || ''}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {packets.length === 0 && (
                <div className="text-center py-4 text-gray-500">
                  No packets captured yet. Start the capture to see network traffic.
                </div>
              )}
            </div>
          )}

          {activeTab === 'attacks' && (
            <div className="space-y-4">
              {detectedAttacks.length > 0 ? (
                detectedAttacks.map((attack, i) => (
                  <div key={i} className="border-l-4 border-red-500 bg-red-50 p-4">
                    <div className="flex justify-between items-start">
                      <div>
                        <h4 className="font-semibold text-red-800">DDoS Attack Detected</h4>
                        <p className="text-sm text-gray-600">
                          Source: {attack.source} | Target: {attack.target} | Type: {attack.type}
                        </p>
                        <p className="text-xs text-gray-500 mt-1">
                          {new Date(attack.timestamp).toLocaleString()}
                        </p>
                      </div>
                      <div className="flex gap-2">
                        <button
                          className="px-3 py-1 bg-red-100 text-red-700 text-sm rounded hover:bg-red-200"
                          onClick={() => handleMitigate(attack.source, 'block')}
                        >
                          Block Source
                        </button>
                        <button
                          className="px-3 py-1 bg-green-100 text-green-700 text-sm rounded hover:bg-green-200"
                          onClick={() => handleMitigate(attack.source, 'rate_limit')}
                        >
                          Rate Limit
                        </button>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="text-center py-8 text-gray-500">
                  No attacks detected. The network appears to be operating normally.
                </div>
              )}
            </div>
          )}

          {activeTab === 'mitigations' && (
            <div>
              {Object.keys(activeMitigations).length > 0 ? (
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Target</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Action</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Applied At</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Expires</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {Object.entries(activeMitigations).map(([target, mitigation]) => (
                      <tr key={target}>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {target}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {mitigation.action}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {new Date(mitigation.timestamp).toLocaleString()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {new Date(mitigation.expires).toLocaleString()}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <div className="text-center py-8 text-gray-500">
                  No active mitigations.
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const NetworkMonitor = () => {
  const [permissionsGranted, setPermissionsGranted] = useState(false);

  const handlePermissionsGranted = useCallback(() => {
    setPermissionsGranted(true);
  }, []);

  if (!permissionsGranted) {
    return <PermissionRequest onPermissionGranted={handlePermissionsGranted} />;
  }

  return (
    <ErrorBoundary>
      <NetworkMonitorContent />
    </ErrorBoundary>
  );
};

export default NetworkMonitor;
